 */
class AttendanceRecordTest extends PHPUnit_Framework_TestCase {
    /**
     * @var    AttendanceRecord
     * @access protected
     */
    protected $AttendanceObj;

    /**
     * Runs the test methods of this class.
     *
     * @access public
     * @static
     */
    public static function main() {
        require_once 'PHPUnit/TextUI/TestRunner.php';

        $suite  = new PHPUnit_Framework_TestSuite('AttendanceRecordTest');
        $result = PHPUnit_TextUI_TestRunner::run($suite);
    }

    /**
     * Sets up the fixture, for example, opens a network connection.
     * This method is called before a test is executed.
     *
     * @access protected
     */
    protected function setUp() {

        $this->AttendanceObj = new AttendanceRecord;

        $conf = new Conf();
    	$this->connection = mysql_connect($conf->dbhost.":".$conf->dbport, $conf->dbuser, $conf->dbpass);
        $this->assertTrue(mysql_select_db($conf->dbname));

        $this->_runQuery("TRUNCATE TABLE `hs_hr_employee`");
        $this->_runQuery("TRUNCATE TABLE `hs_hr_attendance`");

        $this->_runQuery("INSERT INTO `hs_hr_employee` (`emp_number`, `emp_lastname`, `emp_firstname`, `emp_nick_name`, `coun_code`) VALUES ('001', 'Kayla', 'Abbey', 'Kayla', 'US')");
		$this->_runQuery("INSERT INTO `hs_hr_attendance` VALUES ('1', '001', '2008-12-16 08:00:00', '2008-12-16 08:30:00', 'Note1')");

    }

    /**
     * Tears down the fixture, for example, closes a network connection.
     * This method is called after a test is executed.
     *
     * @access protected
     */
    protected function tearDown() {}

	/**
	 * Run given sql query
	 */

	private function _runQuery($sql) {
	    $this->assertTrue(mysql_query($sql), mysql_error());
	}

    public function testUpdateRecord() {

    	$dataObj = new AttendanceRecord();
    	$dataObj->setAttendanceId(1);
    	$dataObj->setFromTime("2008-12-16 08:15:00");

    	$attendanceObj = new AttendanceRecord();
    	$attendanceObj->updateRecord($dataObj);

    	$result = mysql_query("SELECT * FROM `hs_hr_attendance` WHERE `attendance_id` = 1");

    	$row = mysql_fetch_array($result);

    	$this->assertEquals("2008-12-16 08:15:00", $row['punchin_time']);
    	$this->assertEquals("2008-12-16 08:30:00", $row['punchout_time']);
    	$this->assertEquals("Note1", $row['note']);

    }
}

